  data: function data() {
    return {
      titleImg: "img/product01.png",
      img: "https://img0.uzaicdn.com/ba/sightGallery/ATT0000806749.jpg?imageView2/2/w/550/h/413/format/jpg/interlace/1",
      ac: true,
      msg: false,
      items: [{
        img: "https://img4.uzaicdn.com/ba/sightGallery/ATT0000806749.jpg?imageView2/2/w/182/h/137/format/jpg/interlace/1",
        ac: true
      }, {
        img: "https://img0.uzaicdn.com/ba/sightGallery/鼓浪环岛447鼓浪环岛路446+视觉中国+RF+VCG21gic19991303.jpg?imageView2/2/w/182/h/137/format/jpg/interlace/1",
        ac: false
      }, {
        img: "https://img4.uzaicdn.com/ba/sightGallery/曾厝垵22919曾厝垵30598+视觉中国+RF+VCG21gic20062182.jpg?imageView2/2/w/182/h/137/format/jpg/interlace/1",
        ac: false
      }, {
        img: "https://img1.uzaicdn.com/ba/sightGallery/万国建筑博览423万国建筑博览422+视觉中国+RF+VCG2120d318f4b.jpg?imageView2/2/w/182/h/137/format/jpg/interlace/1",
        ac: false
      }],
      act_time: false,
      // 成人数量
      man_num: 1,
      // 孩子数量
      child_num: 0,
      // 景点名称 
      gtitle: "sss",
      gimg: "https://img0.uzaicdn.com/ba/sightGallery/ATT0000806749.jpg?imageView2/2/w/550/h/413/format/jpg/interlace/1",
      // 地点
      gmap: "",
      def_time: "请选择出团日期",
      godata: "",
      // 景点价格
      man_price: 0,
      local: "北京",
      chostime: [{
        tit: "09-26(周四)出发 - 3660/人 - A行程",
        godata: "2019-09-26",
        ptime: false
      }, {
        tit: "10-01(周二)出发 - 4460/人 - A行程",
        godata: "2019-10-01",
        ptime: false
      }, {
        tit: "10-02(周三)出发 - 4460/人 - A行程",
        godata: "2019-10-02",
        ptime: false
      }, {
        tit: "10-08(周二)出发 - 4060/人 - A行程",
        godata: "2019-10-08",
        ptime: false
      }, {
        tit: "10-10(周四)出发 - 4060/人 - A行程",
        godata: "2019-10-10",
        ptime: false
      }, {
        tit: "10-12(周六)出发 - 4060/人 - A行程",
        godata: "2019-10-12",
        ptime: false
      }, {
        tit: "10-30(周三)出发 - 4460/人 - A行程",
        godata: "2019-10-30",
        ptime: false
      }],
      res_obj: {}
    };
  },
  // 获取数据库信息放到页面中
  created: function created() {
    var _this = this;

    // 获取数据库信息
    var get_url = "order/getload";
    this.axios.get(get_url).then(function (res) {
      var goods = res.data[0];
      _this.gtitle = goods.gtitle;
      _this.man_price = goods.g_m_price;
      _this.gmap = goods.gmap;
    });
  },
  beforeDestroy: function beforeDestroy() {
    this.bus.$emit('getpinfo', this.res_obj);
  },
  watch: {
    def_time: {
      handler: function handler(newval) {
        if (newval != "请选择出团日期") {
          this.msg = false;
        }
      }
    }
  },
  methods: {
    changeImg: function changeImg(index) {
      this.img = this.items[index].img;

      for (var i = 0; i < this.items.length; i++) {
        if (i == index) {
          this.items[index].ac = true;
          this.gimg = this.items[index].img;
          console.log(this.gimg);
        } else {
          this.items[i].ac = false;
        }
      }
    },
    showtime: function showtime(e) {
      //console.log(e.currentTarget);
      //e.currentTarget.getElementById("string") 
      if (e.currentTarget.firstChild.className == "li") {
        if (this.act_time == false) {
          this.act_time = true;
          return;
        }

        if (this.act_time == true) {
          this.act_time = false;
          return;
        }
      }
    },
    choosetime: function choosetime(index) {
      this.def_time = this.chostime[index].tit;
      this.gdata = this.chostime[index].godata;
      console.log(this.gdata);
    },
    // 提交订单
    order: function order() {},
    // 人的加减
    reduce: function reduce(e) {
      if (e == "m" && this.man_num > 1) {
        this.man_num--;
      } else if (e == "c" && this.child_num > 0) {
        this.child_num--;
      }
    },
    add: function add(e) {
      if (e == "m") {
        this.man_num++;
      } else if (e == "c") {
        this.child_num++;
      }
    },
    reserve: function reserve() {
      if (this.def_time === "请选择出团日期") {
        this.msg = true;
        return;
      } else {
        this.msg = false;
      }

      var oid = 'PD' + new Date().getTime();
      var gimg = this.gimg;
      var gtitle = this.gtitle;
      var gdata = this.gdata; //console.log(oid)

      var man_count = this.man_num;
      var child_count = this.child_num;
      var man_price = this.man_price;
      var child_price = this.man_price - 200;
      var mans_count = this.man_num + this.child_num; // let godata=this.godata;

      var local = this.gmap;
      var choosetime = this.gdata;
      var obj2 = {
        oid: oid,
        man_count: man_count,
        child_count: child_count,
        man_price: man_price,
        child_price: child_price,
        mans_count: mans_count,
        gtitle: gtitle,
        local: local,
        gimg: gimg,
        choosetime: choosetime,
        gdata: gdata
      };
      this.res_obj = obj2;
      this.$router.push('/booking'); // this.$router.push('/booking');
    }
  },
  components: {
    dHeader: dHeader,
    dFooter: dFooter,
    dCbl: dCbl
  }
};
